import React, { Component } from 'react';
import { useParams, useNavigate, withRouter, Link } from 'react-router-dom';
import { Container, Label, Form, FormGroup, Input, Button, Alert } from 'reactstrap';
import goNavigate from './Navigate';



class SignUp extends Component {


    constructor(props){
        super(props);
        this.state = {
            username:"",
            password:"", 
            email:"",
            error:""
        };
    }


    handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
    };

    handleSubmit = async(event) => {
        event.preventDefault();
        const {username, password, email} = this.state;
        try {
            const response = await fetch('/user/signup', {
                method:'POST',
                headers:{
                    'Content-Type': 'application/json',
                },
                body:JSON.stringify({username, password, email}),
            });
            if(!response.ok){
                throw new Error('Sign up failed')
            }
            this.props.history.push('/login');
        } catch (error){
            this.setState({error: 'Sign up failed. Please try again'});
        }
        // this.props.navigation("/profile");
    };

    render() {
        const {username, password, email, error} = this.state;
        return (<div>
            <Container>
                <h2>Sign Up</h2>
                {error && <Alert color="danger">{error}</Alert>}
                <Form onSubmit={this.handleSubmit}>
                    <FormGroup>
                        <Label for="username">Username</Label>
                        <Input type="text" name="username" id="username" value={username}
                        onChange={this.handleChange}/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="email">Email</Label>
                        <Input type="email" name="email" id="email" value={email}
                        onChange={this.handleChange}/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="password">Password</Label>
                        <Input type="password" name="password" id="password" value={password}
                        onChange={this.handleChange}/>
                    </FormGroup>
                    <FormGroup>
                        <Button color='primary' type='submit'>Submit</Button>
                    </FormGroup>
                </Form>
                <p>Already have an account? <Link to="/login">Login here</Link></p>
            </Container>
        </div>
        );
    }
}
export default SignUp;
